// Generated by CoffeeScript 1.10.0
var formVisibilityToggle, loadGroupsFrom, makeGroupSelect;

makeGroupSelect = function(groups) {
  var i;
  $('#group').empty();
  for (i in groups) {
    i = i;
    $('#group').append('<option value="' + groups[i] + '">' + groups[i] + '</option>');
  }
};

loadGroupsFrom = function(link) {
  var year;
  year = $('#year').val();
  $.ajax({
    type: 'GET',
    url: link,
    data: {
      'year': year
    },
    dataType: 'json',
    success: makeGroupSelect
  });
};

formVisibilityToggle = function() {
  var input;
  input = $('input[name="who"]');
  input.change(function() {
    var elem;
    elem = $('input:radio:checked');
    switch (elem.val()) {
      case 'student':
        $('form#student').show();
        $('form#teacher').hide();
        break;
      case 'teacher':
        $('form#teacher').show();
        $('form#student').hide();
        break;
      default:
        break;
    }
  });
};
